import React from 'react'
import { useState } from 'react'

export default function Register() {
  let [username, setUsername] = useState("");
  let [password, setPassword] = useState("");
  let [email, setEmail] = useState("");

  function handleSubmit(event) {
    event.preventDefault();
    alert (`register in with ${username} and ${password} and ${email}`);
  }

  function updateUsername(event) {
    setUsername(event.target.value);
  }
  function updatePassword(event){
    setPassword(event.target.value);
  }
  function updateEmail(event) {
    setEmail(event.target.value)
  }
  return (
    <div>
    <form onSubmit={handleSubmit}>
    <table>
    <input type="text" className='p-5' onChange={updateUsername}></input>
    <input type="password" onChange={updatePassword}></input>
    <input type="email" onChange={updateEmail}></input>

    <input type="submit" value="Register"></input>
    </table>



    </form>
    </div>
  )
}
