import { Link } from 'react-router-dom';

export default function ProductCard(props) {
  const { product, btn, clickHandler, navigateDetail } = props;
  console.log(props);


  return (

    <div>
  <div className="border border-0 text-start" onClick={() => navigateDetail(product.id)} >
        <img src={product.thumbnail} class="card-img-top" alt="..." />
        <div className="card-body">
          <span className="badge rounded-pill text-bg-secondary mt-2">
            {product.brand}
          </span>
          {product.stock == 0 ? 
          <span className="badge bg-danger ms-2 position-absolute top-0 start-0 my-2">Out Of Stock</span> :
        <span className="badge bg-success ms-2 position-absolute top-0 start-0 my-2">In Stock</span>
          }
          <h5 className="card-title row justify-content-center">{product.title}</h5>



          <p className="card-text">{product.description.substr(0,25)}</p>
          {/* <p className="text-success">{product.rating}</p> */}


          {/* <button onClick={() => navigateDetail(product.id)}>view</button> */}

          <button variant="btn btn-outline-success rounded-5 px-4">Add To Cart</button>


          {/* <button onClick={() => clickHandler(product.id)}>{btn}</button> */}
        </div>
        {/* {products.images.map((images) => (
        <img src={images} class="card-img-top" alt="..." />
    ) )} */}

        <div className="card-footer">
          <small className="text-body-secondary">Price : {product.price} $</small>
        </div>
      </div>
    </div>
  );
}
